function downloadFile() {
    const embedElement = document.getElementById("pdf_file");
    const sourceUrl = embedElement.getAttribute("src");
  
    const link = document.createElement("a");
    link.href = sourceUrl;
    link.download = "myfile.pdf";
    link.click();
  }

